export const projects = [
  {
    title: "League of Legends match history",
    description: "This was the first personal project I created, a web application created in Django. Using the Riot Games API I created a simple match history for the popular game League of Legends. It let's players view their 10 most recent matches, see their enemies and teammates, and view their items. Click here to check out the GitHub repo with all of the code, hopefully someday I will have the website up and running to test out.",
    imageSrc: "/images/league.jpg",
    link: "https://github.com/Jonah-Min/Match-History",
  },
  {
    title: "Magic the Gathering web app",
    description: "Magic the gathering app created using ReactJS. This was the app I created to learn ReactJS before I started by Job at HubSpot, and it allows people to view cards, add cards to a deck, and check their mana color and curve. Click here to check out the website and play around with it.",
    imageSrc: "/images/magic.png",
    link: "http://magic.jonahmin.com/",
  },
  {
    title: "League of Legends database project",
    description: "Using the same API as the first League of Legends project, I created a simple web application for my Database design class. I worked on this alongside a friend and this allows you to view champion stats, edit stats, add champions, and delete champtions. Click to see the code.",
    imageSrc: "/images/database.png",
    link: "https://github.com/Jonah-Min/Database-Project",
  },
  {
    title: "SASE web development workshop",
    description: "I held a web development workshop for the Society of Asian Scientists and Engineers where I taught students the basics of HTML, CSS, and JavaScript. Click here to check out the final product of the website.",
    imageSrc: "/images/webdevworkshop.jpg",
    link: "http://workshop.jonahmin.com/",
  },
  {
    title: "Discord bot",
    description: "I created this bot as a way to learn Ruby, it uses several APIs to populate the command responses.",
    imageSrc: "/images/discord.png",
    link: "https://github.com/Jonah-Min/JonahBot",
  },
  {
    title: "This website!",
    description: "I'm very proud of this website, as it was built from scratch using React and completely designed by me. I also had to implement my own lightbox!",
    imageSrc: "/images/newWebsite.png",
    link: "https://github.com/Jonah-Min/NewPersonalWebsite",
  }
];
